import ShowName from "./ShowName";
import TakeName from "./TakeName";
const dummy_data = [
  {
    name: "umair",
    age: "10",
  },
];
function App() {
  const [expenses, setExpenses] = useState(dummy_data);

  const getDataHandler = (expense) => {
    setExpenses((prevExpenses) => {
      return [expense, ...prevExpenses];
    });
  };

  return (
    <div>
      <TakeName onGetData={getDataHandler} />
      <ShowName items={dummy_data} />
    </div>
  );
}

export default App;
